        }, [dispatch, player])


  return (
    <Container>
        <Button variant="primary" onClick={()=> handleAddWin(player)}>
            Add Win
        </Button>

        <Button variant="danger" onClick={ () => handleSubWin(player)}>
            Subtract Win
        </Button>

    </Container>
  )
}

export default PlayerButton
