import React, { useEffect } from "react";
import { Button, Container } from "react-bootstrap";
import { useDispatch } from "react-redux";
import { addWin, subWin } from "../actions/playerActions";

function PlayerButton({ player }) {
  const dispatch = useDispatch();

  const handleAddWin = (player) => {
    if (player && player.wins !== undefined) {
      dispatch(addWin(player.id));
    }
  };

  const handleSubWin = (player) => {
    dispatch(subWin(player.id));
  };

  const logPlayer = () => {
    console.log(player);
  };

  console.log(player)

  useEffect(() => {}, [dispatch, player]);

  return (
    <Container>
      <Button variant="primary" onClick={() => handleAddWin(player.id)}>
        Add Win
      </Button>

      <Button variant="danger" onClick={() => handleSubWin(player.id)}>
        Subtract Win
      </Button>
    </Container>
  );
}

export default PlayerButton;
