import React, { useEffect } from "react";
import { Button, Container } from "react-bootstrap";
import { useDispatch } from "react-redux";
import { addWin, subWin } from "../actions/playerActions";

function PlayerButton({ player }) {
  const dispatch = useDispatch();

  const id = player.id;

  const handleAddWin = (id) => {

      dispatch(addWin(id));
      console.log(id)
  };

  const handleSubWin = (id) => {
    dispatch(subWin(id));
    console.log(id)
  };

  const log = () => {
    console.log();
  };



  useEffect(() => {}, [dispatch,id]);

  return (
    <Container>
      <Button variant="primary" onClick={() => handleAddWin(id)}>
        Add Win
      </Button>

      <Button variant="danger" onClick={() => handleSubWin(id)}>
        Subtract Win
      </Button>
    </Container>
  );
}

export default Button;
