import { ADD_WIN, SUBTRACT_WIN } from '../constants/playerConstants';

const initialState = {
  players: [
    { name: 'Player 1', wins: 0 },
    { name: 'Player 2', wins: 0 },
    { name: 'Player 3', wins: 0 },
  ],
};

export const playerReducer = (state = initialState, action) => {
  switch (action.type) {
    case ADD_WIN:
      return {
        ...state,
        players: state.players.map((player) =>
          player.name === action.payload
            ? { ...player, wins: player.wins + 1 }
            : player
        ),
      };
    case SUBTRACT_WIN:
      return {
        ...state,
        players: state.players.map((player) =>
          player.name === action.payload
            ? { ...player, wins: player.wins - 1 }
            : player
        ),
      };
    default:
      return state;
  }
};
