import { ADD_WIN, SUB_WIN } from "../constants/playerConstants";

export const winAction = (player) => (dispatch) => {
    dispatch({
        type: ADD_WIN,
        payload: {
            name: player.name,
            wins: ++player.wins,
            champion: player.champion,
        },
    });
    dispatch({
        type: SUB_WIN,
        payload: {
            name: player.name,
            wins: --player.wins,
            champion: player.champion,
        },
    });
}
