import { ADD_WIN, SUB_WIN } from "../constants/playerConstants";
import { players } from "../players";

export const winReducer = (state = { wins: [] }, action) => {
    switch (action.type) {
        case ADD_WIN:
            case ADD_WIN:
  return {
    ...state,
    wins: [...state.wins, { name: action.payload.name, wins: 1 }]
  };

        case SUB_WIN:
            return {
                ...state,
                players: state.players.map((player) =>
                    player.name === action.payload.name ? action.payload : player
                ),
            };
        default:
            return state;
    }
};
